	@Override
	public String getMessage() {
	    	String delimeter = "";
	    	String substringOfferName = StringUtil.substringByte(offerName, 0, 40);
			return StringUtils.rightPad(ifCode, 4)+delimeter			// 업무구분(N,4)
	    			+StringUtils.rightPad(dataType, 2)+delimeter		// 데이터구분(N,2) : 05
	    			+StringUtils.leftPad((seq+""), 7, "0")+delimeter	// 일련번호(N,7)
	    			+StringUtils.rightPad(payType, 2)+delimeter			// 결제수단(N,2) : 01 실시간계좌이체
	    			+StringUtils.rightPad(campaignId, 8)+delimeter		// 프로젝트코드
	    			+StringUtils.rightPad(substringOfferName, 40-StringUtil.getHangeulCount(substringOfferName))+delimeter	// 청약종목명
	    			+StringUtils.rightPad((investId+""), 9)+delimeter		// 원거래센터 전문관리번호
	    			+StringUtils.leftPad((investId+""), 20, "0")+delimeter	// 투자자입금관리번호(고객식별자)
	    			+StringUtils.leftPad((investId+""), 7, "0")+delimeter	// 전문일련번호
	    			+StringUtils.leftPad(investAmount, 15, "0")+delimeter	// 청약금액
	    			+StringUtils.rightPad(userId+"", 20)+delimeter			// 투자자ID
	    			+StringUtils.rightPad(paymentsOfStocksYn, 1)+delimeter	// 주금납입여부
	    			+StringUtils.rightPad(payReqDate, 8)+delimeter			// 결재요청일자
	    			+StringUtils.rightPad(payReqTime, 6)+delimeter			// 결재요청시간
	    			+StringUtils.rightPad(filler, 51);						// Filler
	    }

